  "artifact_path": "digital-bank-monorepo",
  "file_structure": {
    "name": "digital-bank-monorepo",
    "type": "directory",
    "children": [
      {
        "name": "apps",
        "type": "directory",
        "children": [
          {
            "name": "api",
