  "ui_patterns": [
    "Optimistic UI Updates",
    "Infinite Scroll Feed",
    "Skeleton Loading States",
    "Toast Notifications",
    "Modal Dialogs",
    "Drawer Navigation (Mobile)",
    "Real-time Badge Counters"
  ],
  "component_specs": [
