      "name": "ChatWindow",
      "description": "Real-time messaging interface with history and input.",
      "props": "{\"recipient\": \"User\", \"messages\": \"Message[]\", \"onSend\": \"function\", \"isTyping\": \"boolean\"}",
      "states": [
        "connected",
        "connecting",
        "disconnected"
      ],
      "variants": [
        "floating",
        "fullscreen"
      ]
    }
  ],
  "layout_breakpoints": {
    "2xl": "1536px",
    "lg": "1024px",
    "md": "768px",
    "sm": "640px",
    "xl": "1280px"
  },
