    "aria_labels": true,
    "checklist": [
      "All images have alt text",
      "Color contrast ratio > 4.5:1",
      "Interactive elements min size 44x44px",
      "Focus visible on all inputs"
    ],
    "focus_indicators": true,
    "guidelines": [
      "Use semantic HTML5 elements",
      "Ensure logical tab order",
      "Provide skip links for navigation"
    ],
    "keyboard_navigation": true,
    "screen_reader_support": true,
    "standard": "WCAG 2.1",
    "wcag_level": "AA"
  },
  "atomic_structure": {
    "atoms": [
      "Avatar",
      "Button",
      "Icon",
      "Input",
      "Typography",
      "Badge",
      "Spinner"
    ],
    "molecules": [
      "UserListItem",
      "CommentRow",
      "SearchBar",
      "NotificationItem",
      "MediaPreview",
      "ActionToolbar"
    ],
    "organisms": [
      "PostCard",
      "FeedStream",
      "SidebarNavigation",
      "ChatWindow",
      "CreatePostModal",
      "ProfileHeader"
    ]
  },
  "website_layout": {
    "pages": [
      {
        "name": "Home Feed",
        "route": "/feed",
        "sections": [
          {
            "name": "LeftSidebar",
            "components": [
              "Logo",
              "NavMenu",
              "UserMiniProfile"
            ]
          },
          {
            "name": "MainFeed",
            "components": [
              "CreatePostComposer",
              "FeedStream"
            ]
          },
          {
            "name": "RightWidgets",
            "components": [
              "TrendingTopics",
              "WhoToFollow"
            ]
          }
        ]
      },
      {
        "name": "Profile",
        "route": "/u/:username",
        "sections": [
          {
            "name": "Header",
            "components": [
              "ProfileCover",
              "ProfileStats",
              "EditProfileButton"
            ]
          },
          {
            "name": "Content",
            "components": [
              "PostGrid",
              "MediaGallery"
            ]
          }
        ]
      },
      {
        "name": "Messages",
        "route": "/messages",
        "sections": [
          {
            "name": "ConversationList",
            "components": [
              "SearchBar",
              "InboxList"
            ]
          },
          {
            "name": "ActiveChat",
            "components": [
              "ChatHeader",
              "MessageList",
              "MessageInput"
            ]
          }
        ]
      }
